(
	#{MENU_ID}
	,#{upprMenuId}
	,#{menuname}
	,#{writerId}
	,#{ordr}
	,SYSDATE
)

mapper.delete:
/* 호출ID : deleteMenu - 메뉴 삭제하기 */
DELETE FROM MENU
WHERE MENU_ID = #{menuId}
